import { Component, OnInit } from '@angular/core';
import { CategoriasService } from 'src/app/services/categorias.service';

@Component({
  selector: 'app-body',
  templateUrl: './body.component.html',
  styleUrls: ['./body.component.css']
})
export class BodyComponent implements OnInit{

  categorias: any;
  arrCategorias: any;

  constructor(private _categorias: CategoriasService) { }

  ngOnInit(): void {
    this.obtenerCategorias();
  }

  obtenerCategorias() {
    this._categorias.obtenerCategorias()
              .subscribe ( (respuesta: any) => {
                  this.categorias = respuesta;
                  this.arrCategorias = this.categorias.categoriaResponse.categoria;
                  console.log( this.arrCategorias);
              })
  }

}
<br>
<div class="container">
    <table class="table">
        <thead>
            <tr>
                <th scope="col">#</th>
                <th scope="col">Nombre</th>
                <th scope="col">Descripcion</th>
            </tr>
        </thead>
        <tbody>
            <tr *ngFor="let item of arrCategorias">
                <th scope="row"> {{ item.id }}</th>
                <td> {{ item.nombre }}</td>
                <td> {{ item.descripcion }}</td>
            </tr>

        </tbody>
    </table>
</div>
